@section('javascript')
<script>
    $(document).ready(function() {
        // Automatically close the message after 5 seconds
        setTimeout(function() {
            $('#success-message').fadeOut('slow', function() {
                $(this).remove();
            });
        }, 5000); // 5000ms = 5 seconds
    });
</script>
<script>
    $(document).ready(function() {
        // Handle View Button Click
        $(document).on('click', '.view-btn', function() {
            // Retrieve job data directly from the button
            var job = $(this).data("job");

            console.log("View Button Clicked:", job); // Log button click and job data

            // Check if job data exists before accessing properties
            if (job) {
                // Set modal content with job details
                $("#modalJobRole").text(job.job_role);
                $("#modalJobDescription").html(job.job_description);
                $("#modalJobPayRange").text(`$${job.pay_min} - $${job.pay_max}`);

                // Show the Job Detail Modal
                $('#jobDetailModal').modal('show');
            } else {
                console.error('No job data found.');
            }
        });

        // Handle Apply Button Click
        $(document).on('click', '.apply-btn', function() {
            var job = $(this).data("job");

            // Set job_id in the form
            $("#applyForm #job_id").val(job.id);
            $("#applyForm #job_role").val(job.job_role);

        });
    });
</script>
@endsection